      [ 'horizontalAbsolute', 0 ],
      [ 'write', 'S      \\ |----|\n' ],
      [ 'show' ] ])
  cursor = []
  bar.last = new Date(0)
  bar.pulse("P")
  isOutput(t, "pulsed spinner with subsection",
    [ [ 'up', 1 ],
      [ 'hide' ],
      [ 'horizontalAbsolute', 0 ],
      [ 'write', 'S -> P | |----|\n' ],
      [ 'show' ] ])
})

